defaultCss:
@import url("https://fonts.googleapis.com/css2?family=Geom:ital,wght@0,300..900;1,300..900&display=swap");
:root { --accent: 133,100,255; --tint: 17,14,30; }
body { font-family: "Geom", sans-serif; font-size: 1.8vh; color: #cdd6f4; background-color: #1e1e2e; background-image: url("/SkinManager/images/catppuccin.webp"); background-size: cover; background-position: center; background-repeat: no-repeat; }
a { color: #f5c2e7; }
a:hover { color: #cdd6f4; }

@import url("https://fonts.googleapis.com/css2?family=Geom:ital,wght@0,300..900;1,300..900&display=swap");
body { font-family: "Geom", sans-serif; font-size: 1.8vh; color: #ffffff; background-color: #111111; background-image: url("/SkinManager/images/elegantfin.webp"); background-size: cover; background-position: center; background-repeat: no-repeat; }
.textActionButton:hover { color: rgb(133,100,255); }

@import url("https://fonts.googleapis.com/css2?family=Roboto:wght@300;400;700&display=swap");
body { font-family: "Roboto", sans-serif; color: #e0e0e0; background-color: #181818; background-image: url("/SkinManager/images/finimalism.png"); background-size: cover; background-position: center; background-repeat: no-repeat; }
h1,h2,h3 { color: #ffffff; }
a { color: #61afef; }
a:hover { color: #c678dd; }

body { font-family: "Segoe UI", sans-serif; color: #dddddd; background-color: #2b2b2b; background-image: url("/SkinManager/images/zesty.jpg"); background-size: cover; background-position: center; background-repeat: no-repeat; }
a, .textActionButton { color: #ff6c6b; }
a:hover, .textActionButton:hover { color: #ffffff; }

body { font-family: "Arial", sans-serif; color: #f0f0f0; background-color: #0d0d0d; background-image: url("/SkinManager/images/zombie.png"); background-size: cover; background-position: center; background-repeat: no-repeat; }
a, .textActionButton { color: #e63946; }
a:hover, .textActionButton:hover { color: #ffffff; }
